import { StyleSheet } from "react-native";

import {
  gameCardBackgroundColor,
  gameCardShadowColor,
  subtitleTextColor,
} from "../../styles";

export const labelFontSize = 14;
export const subtitleFontSize = 10;

const styles = StyleSheet.create({
  card: {
    width: 152,
    height: 209,
    paddingTop: 15,
    paddingRight: 8,
    paddingBottom: 8,
    paddingLeft: 8,
    backgroundColor: gameCardBackgroundColor,
    justifyContent: "center",
    alignItems: "center",
    shadowColor: gameCardShadowColor,
    shadowOffset: { width: 1, height: 0 },
    shadowRadius: 5,
    shadowOpacity: 0.4,
    borderRadius: 2,
  },
  bottomPanel: {
    marginTop: "auto",
    flexDirection: "row",
  },
  friends: {
    flexGrow: 1,
    flexDirection: "row",
    alignItems: "center",
  },
  likes: {
    flexDirection: "row",
    alignItems: "center",
  },
  subtitle: {
    color: subtitleTextColor,
    opacity: 0.6,
    marginLeft: 4,
    fontFamily: "Agrandir-Regular",
    fontWeight: "400",
    lineHeight: 1.4 * subtitleFontSize,
    letterSpacing: 0.06 * subtitleFontSize,
  },
  label: {
    position: "absolute",
    top: 0,
    right: 0,
    width: 62,
    height: 22,
    backgroundColor: subtitleTextColor,
    borderRadius: 2,
    borderWidth: 0.5,
    borderStyle: "solid",
    borderColor: "#161E2C",
    zIndex: 1,
    textAlign: "center",
    fontWeight: "500",
    fontFamily: "Agrandir-Medium",
    fontSize: labelFontSize,
    lineHeight: 1.2857 * labelFontSize,
    letterSpacing: 0.06 * labelFontSize,
  },
  thumbs: {
    opacity: 0.6,
  },
});

export default styles;
