import {
  View,
  Text,
  Image,
  StyleSheet,
  ImageSourcePropType,
  TouchableOpacity,
} from 'react-native';
import React from 'react';
import colors from '../assets/colors';

import like from '../assets/images/likeIcon.png';
import {moderateScale} from 'react-native-size-matters';

import {height, width} from '../utils/platform';
interface ICard {
  item: {
    title: string;
    img: ImageSourcePropType;
    friends: string;
    likes: string;
    msg?: number;
  };
}

const Card = ({item}: ICard) => {
  return (
    <TouchableOpacity style={styles.cardContainer} activeOpacity={0.9}>
      {item?.msg && (
        <View style={styles.notifiContainer}>
          <Text style={styles.notifiText}>{item?.msg} New</Text>
        </View>
      )}

      <View style={styles.cardImageContainer}>
        <Image
          resizeMode="contain"
          style={styles.cardImage}
          source={item.img}
        />
      </View>
      <View style={styles.cardDetailsContainer}>
        <Text style={styles.cardDetailsText} numberOfLines={2}>
          {item?.title}
        </Text>
        <View style={styles.cardBottomContainer}>
          <View style={styles.cardBottomTextContainer}>
            <Image
              style={styles.cardBottomImage}
              source={like}
              resizeMode="contain"
            />

            <Text style={styles.cardBottomText}> {item.friends} friends</Text>
          </View>
          <View style={styles.cardBottomTextContainer}>
            <Image
              style={styles.cardBottomImage}
              source={like}
              resizeMode="contain"
            />

            <Text style={styles.cardBottomText}> {item.likes} M</Text>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );
};

export default Card;

const styles = StyleSheet.create({
  cardContainer: {
    backgroundColor: colors.cardColor,
    width: width / 4.5,
    height: height / 2.5,
    alignItems: 'center',
    borderRadius: 2,
    shadowColor: colors.black,
    shadowOpacity: 0.4,
    shadowRadius: 2,
    shadowOffset: {width: 2, height: 2},
    elevation: 2,
    marginVertical: height / 80,
    marginHorizontal: width / 80,
  },
  notifiContainer: {
    position: 'absolute',
    right: 0,
    top: 0,
    zIndex: 1,
    backgroundColor: colors.neutralColor,
    width: width / 15,
    height: height / 30,
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 2,
  },
  notifiText: {
    fontSize: moderateScale(8),
    fontFamily: 'Agrandir-Regular',
  },
  cardImageContainer: {
    width: '80%',
    height: '60%',
    borderRadius: 1,
    marginTop: height / 45,
  },
  cardImage: {
    width: '100%',
    height: '100%',
  },
  cardDetailsContainer: {
    width: '90%',
    flex: 1,
    justifyContent: 'space-between',
    paddingVertical: height / 70,
  },
  cardDetailsText: {
    color: colors.neutralColor,
    fontSize: moderateScale(11),
    paddingHorizontal: 15,
    fontFamily: 'Agrandir-Regular',
  },
  cardBottomContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 5,
  },
  cardBottomTextContainer: {
    flexDirection: 'row',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  cardBottomImage: {
    height: 15,
    width: 15,
  },
  cardBottomText: {
    fontSize: moderateScale(11),
    color: colors.neutralColor,
    opacity: 0.6,
    fontFamily: 'Agrandir-Regular',
    paddingTop: 5,
  },
});
