const formatNumber = (number) => {
  if (number < 1e3) {
    return `${number}`;
  }
  if (number < 1e6) {
    number /= 1e2;
    number = Math.trunc(number);
    return `${number / 10} K`;
  }
  if (number < 1e9) {
    number /= 1e5;
    number = Math.trunc(number);
    return `${number / 10} M`;
  }
  if (number === 1e9) {
    return "1 B";
  }
  if (number > 1e9) {
    return ">1 B";
  }
};

export default formatNumber;
