import { View, Image, Text } from "react-native";

import s from "./styles";
import GameCardLava from "../GameCardLava/GameCardLava";
import formatNumber from "../../utils/formatNumbers";

const GameCardHome = ({ uri, title, friends, likes, labelNew = 0 }) => {
  return (
    <View style={s.card}>
      {labelNew > 0 && <Text style={s.label}>{labelNew} New</Text>}
      <GameCardLava uri={uri} title={title} />
      <View style={s.bottomPanel}>
        <View style={s.friends}>
          <Image
            style={s.thumbs}
            source={require("")}
          />
          <Text style={s.subtitle}>
            {(friends && formatNumber(friends)) ?? 0} friend
            {(friends === 0 || friends > 1) && "s"}
          </Text>
        </View>
        <View style={s.likes}>
          <Image
            style={s.thumbs}
            source={require("../../assets/like_icon.png")}
          />
          <Text style={s.subtitle}>{(likes && formatNumber(likes)) ?? 0}</Text>
        </View>
      </View>
    </View>
  );
};

export default GameCardHome;
